import * as react from 'react';
import { HTMLProps } from 'react';

type AcceptProp = Record<string, string[]>;
type DropzoneOptions = {
    multiple?: boolean;
    accept?: AcceptProp;
    minSize?: number;
    maxSize?: number;
    maxFiles?: number;
    disabled?: boolean;
    onDrop: <T extends File>(acceptedFiles: T[]) => void;
};
type DropzoneState = {
    isFocused: boolean;
    isDragActive: boolean;
    isDragAccept: boolean;
    isDragReject: boolean;
    isFileDialogActive: boolean;
    acceptedFiles: File[];
};

type DropEvent = Event | React.DragEvent<HTMLElement> | React.ChangeEvent<HTMLElement>;
/**
 * A React hook that creates a drag 'n' drop area.
 *
 * ### Example
 *
 * ```tsx
 * function MyDropzone() {
 *   const { getRootProps, getInputProps } = useDropzone({
 *     onDrop: acceptedFiles => {
 *       // do something with the File objects, e.g. upload to some server
 *     }
 *   });
 *
 *   return (
 *     <div {...getRootProps()}>
 *       <input {...getInputProps()} />
 *       <p>Drag and drop some files here, or click to select files</p>
 *     </div>
 *   )
 * }
 * ```
 */
declare function useDropzone({ accept, disabled, maxSize, minSize, multiple, maxFiles, onDrop, }: DropzoneOptions): {
    getRootProps: () => HTMLProps<HTMLDivElement>;
    getInputProps: () => HTMLProps<HTMLInputElement>;
    rootRef: react.RefObject<HTMLDivElement>;
    isFocused: boolean;
    isDragActive: boolean;
    isDragAccept: boolean;
    isDragReject: boolean;
    isFileDialogActive: boolean;
    acceptedFiles: File[];
};

export { type AcceptProp, type DropEvent, type DropzoneOptions, type DropzoneState, useDropzone };
